import React, { useEffect, useRef } from 'react';
import p5 from 'p5';

const Sketch = ({ image, stripes, randomSeed, onSave }) => {
  const sketchRef = useRef();


  useEffect(() => {
    const sketch = (p) => {
      let img;

      p.preload = () => {
        if (image) {
          img = p.loadImage(image);
        }
      };

      p.setup = () => {
        p.createCanvas(600, 400);
        p.noLoop();
        drawSketch();
      };

      const drawSketch = () => {
        p.clear();
        p.randomSeed(randomSeed);
        if (img) {
          img.loadPixels();
          let y = 0;
          while (y < p.height) {
            let stripeHeight = p.random(10, 50);
            let posY = y / stripeHeight;
            let colorIndex = parseInt(p.map(posY, 0, stripes, 0, img.height));
            let c = img.get(parseInt(img.width / 2), colorIndex);
            p.fill(c);
            p.noStroke();
            p.rect(0, y, p.width, stripeHeight);
            y += stripeHeight;
          }
          p.filter(p.BLUR, 8); // Применение размытия
        }
      };

      // Функция сохранения холста
      const saveCanvasImage = () => {
        myp5.saveCanvas('gradient_image', 'png');
      };

      // Перерисовка холста при изменении пропсов
      p.redrawSketch = () => {
        drawSketch();
      };
    };

    const myp5 = new p5(sketch, sketchRef.current);

    // Передаем функцию сохранения в родительский компонент
    onSave(saveCanvasImage);

    return () => {
      myp5.remove();
    };
  }, [image, stripes, randomSeed, onSave]);

  return <div ref={sketchRef}></div>;
};

export default Sketch;
