import React, { useEffect, useRef } from 'react';
import p5 from 'p5';

const Sketch = ({ image, stripes, randomSeed }) => {
  const sketchRef = useRef();

  useEffect(() => {
    let p5Instance;

    const sketch = (p) => {
      let img;

      p.preload = () => {
        if (image) {
          img = p.loadImage(image);
        }
      };

      p.setup = () => {
        p5Instance = p;
        p.createCanvas(600, 400);
        p.noLoop();
      };

      p.draw = () => {
        p.clear();
        p.randomSeed(randomSeed);
        if (img) {
          img.loadPixels();
          let y = 0;
          while (y < p.height) {
            let stripeHeight = p.random(10, 50); // случайная высота в пределах от 10 до 50
            let posY = y / stripeHeight;
            let colorIndex = parseInt(p.map(posY, 0, stripes, 0, img.height));
            let c = img.get(parseInt(img.width / 2), colorIndex);
            p.fill(c);
            p.noStroke();
            p.rect(0, y, p.width, stripeHeight);
            y += stripeHeight;
          }
        }
      };

      p.applyBlur = () => {
        p.filter(p.BLUR, 8); // Применение размытия
      };
    };

    p5Instance = new p5(sketch, sketchRef.current);

    return () => {
      p5Instance.remove();
    };
  }, [image, stripes, randomSeed]);

  useEffect(() => {
    if (sketchRef.current) {
      sketchRef.current.childNodes[0].applyBlur();
    }
  }, [stripes, randomSeed]);

  return <div ref={sketchRef}></div>;
};

export default Sketch;
